import { Component, AfterViewInit, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Movie } from 'src/app/models/movie';
import { MoviePlayService } from 'src/app/services/movie-play.service';
import videojs from 'video.js';

@Component({
  selector: 'app-movie-play',
  templateUrl: './movie-play.component.html',
  styleUrls: ['./movie-play.component.css']
})
export class MoviePlayComponent implements AfterViewInit ,OnInit {
  public vjs: videojs.Player;
  urlVideo: string = "/assets/videos/Joker.mp4";
  urlPoster: string = "https://1.bp.blogspot.com/-WsNSf6jZvS0/XM3YV3O4ZDI/AAAAAAAACJ8/A7K0sxMcw_gb82SO9p7JYoAW9tPIIWxRQCKgBGAs/w0/john-wick-3-keanu-reeves-uhdpaper.com-4K-23.jpg";
pictureUrl:string;
  constructor(private moviePlayService:MoviePlayService, private activatedRoute:ActivatedRoute) { }
  ngOnInit(): void {
    this.activatedRoute.params.subscribe(params =>{
      this.getMovie(params["id"]);
      this.pictureUrl = this.movie.pictureUrl;
    });
    console.log(this.movie)
    console.log(this.movie.pictureUrl);
  }

  ngAfterViewInit(): void {

    console.log(this.movie.pictureUrl);
    const options = {
      'sources' : [{
        'src' : this.urlVideo,
        'type' : 'video/mp4'
        }
      ],
      'poster' : this.urlPoster, 
      autoplay:false,
      html5: {
        nativeVideoTracks: false,
        nativeAudioTracks: false,
        nativeTextTracks: false,
        hls: {
            withCredentials: false,
            overrideNative: true,
            debug: true
        }
    },
    TextTrack: [{
      src: '/assets/subtitles/para.vtt', 
      kind:'captions', 
      srclang: 'en', 
      label: "English"
    }
  ] ,


    };
    this.vjs = videojs('my-player', options);
  }
  movie:Movie;


  getMovie(movieId:number){
    this.moviePlayService.getMovie(movieId).subscribe(response => {
     this.movie = async response;
     console.warn(this.movie);
   });
   }

}
